import{_ as e,o as r,c as a,Q as t}from"./chunks/framework.7446e1aa.js";const f=JSON.parse('{"title":"91.02 Awesome Resources","description":"","frontmatter":{},"headers":[],"relativePath":"notes/90-99--Miscellaneous/91--Curation/91.02-Awesome-Resources.md","filePath":"notes/90-99--Miscellaneous/91--Curation/91.02-Awesome-Resources.md","lastUpdated":1727010034000}'),o={name:"notes/90-99--Miscellaneous/91--Curation/91.02-Awesome-Resources.md"},i=t('<h1 id="_91-02-awesome-resources" tabindex="-1">91.02 Awesome Resources <a class="header-anchor" href="#_91-02-awesome-resources" aria-label="Permalink to &quot;91.02 Awesome Resources&quot;">​</a></h1><p>Here are some of my favourite resources I really appreciate to learn or quickly switch technologies.</p><h2 id="generic-ones" tabindex="-1">Generic Ones <a class="header-anchor" href="#generic-ones" aria-label="Permalink to &quot;Generic Ones&quot;">​</a></h2><ul><li><a href="https://goalkicker.com" target="_blank" rel="noreferrer">GoalKicker - Free Programming Books</a> - Short notes compiled from StackOverflow.</li><li><a href="http://learnxinyminutes.com" target="_blank" rel="noreferrer">Learn X in Y Minutes</a> - community-driven one-pager documentation for any language / tool.</li><li><a href="https://news.ycombinator.com/" target="_blank" rel="noreferrer">Hackernews</a> - Couldn&#39;t go wrong this time.</li><li><a href="https://wiki.nikiv.dev/" target="_blank" rel="noreferrer">wiki.nikiv.dev</a> - Everyday collection of various topics with explicitly selective link collection, in-and-out of tech.</li><li>Blogs listed in my <a href="/site/awesome/truly/blogs.html">Awesome Blogs</a> page.</li><li><a href="https://roadmap.sh" target="_blank" rel="noreferrer">roadmap.sh</a> - The community driven widely-accepted developer roadmaps.</li><li><a href="https://github.com/dbrgn/tealdeer" target="_blank" rel="noreferrer">TealDeer</a> - Simplified, example based and community-driven man pages.</li></ul><h2 id="specific-ones" tabindex="-1">Specific Ones <a class="header-anchor" href="#specific-ones" aria-label="Permalink to &quot;Specific Ones&quot;">​</a></h2><h3 id="regex" tabindex="-1">Regex <a class="header-anchor" href="#regex" aria-label="Permalink to &quot;Regex&quot;">​</a></h3><ul><li><a href="https://github.com/ziishaned/learn-regex" target="_blank" rel="noreferrer">ziishaned/learn-regex</a> - Learn regex the easy way.</li><li><a href="https://regex101.com" target="_blank" rel="noreferrer">regex101.com</a> - Easily create &amp; test regex patterns with multiple language implementations.</li></ul><h3 id="git" tabindex="-1">Git <a class="header-anchor" href="#git" aria-label="Permalink to &quot;Git&quot;">​</a></h3><ul><li><a href="https://opensource.com/article/22/11/git-concepts" target="_blank" rel="noreferrer">Basic Git Concepts</a></li><li><a href="/site/blogs/git.html">My blog</a> - Stuffs used all the time</li><li><a href="https://opensource.com/article/22/11/git-tools" target="_blank" rel="noreferrer">Advanced Git tools</a> - reflog/gc/bisect/worktree</li><li><a href="https://ohshitgit.com" target="_blank" rel="noreferrer">OhShitGit</a> - Your rescue to git if you messed it all up!</li></ul><h3 id="algorithms" tabindex="-1">Algorithms <a class="header-anchor" href="#algorithms" aria-label="Permalink to &quot;Algorithms&quot;">​</a></h3><ul><li><a href="https://cp-algorithms.com" target="_blank" rel="noreferrer">CP Algorithms</a> - CP Algorithms explained in details with different variations.</li><li><a href="https://the-algorithms.com" target="_blank" rel="noreferrer">The Algorithms</a> - Clean implmentation of every algorithm in all the languages.</li></ul><h3 id="databases" tabindex="-1">Databases <a class="header-anchor" href="#databases" aria-label="Permalink to &quot;Databases&quot;">​</a></h3><ul><li><a href="https://www.youtube.com/watch?v=W2Z7fbCLSTw" target="_blank" rel="noreferrer">Different Database Paradigms - Fireship.io (YouTube)</a>.</li><li><a href="https://www.youtube.com/watch?v=HubezKbFL7E" target="_blank" rel="noreferrer">Things every developer absolutely, positively needs to know about database indexing - Kai Sassnowski (YouTube)</a>.</li><li><a href="https://www.youtube.com/watch?v=OqCK95AS-YE" target="_blank" rel="noreferrer">What, Why and How to use Redis as your primary database (YouTube)</a> - How to use redis to replace your relational, graph, search, document databases and how to scale.</li></ul><h3 id="linux" tabindex="-1">Linux <a class="header-anchor" href="#linux" aria-label="Permalink to &quot;Linux&quot;">​</a></h3><ul><li><a href="https://www.freecodecamp.org/news/linux-command-line-tutorial" target="_blank" rel="noreferrer">Linux Basics &amp; CLI Tutorial</a> and/or <a href="https://www.codelivly.com/mastering-linux-the-top-commands-every-server-administrator-should-know" target="_blank" rel="noreferrer">Linux Command Cheatsheet</a></li><li><a href="https://www.explainshell.com" target="_blank" rel="noreferrer">ExplainShell.com</a> - Explains the command(s) and their flags, along with meta-characters in the bash.</li><li><a href="https://wiki.archlinux.org" target="_blank" rel="noreferrer">ArchWiki</a> &amp; <a href="https://wiki.gentoo.org/wiki/Main_Page" target="_blank" rel="noreferrer">GentooWiki</a> &amp; <a href="https://docs.voidlinux.org" target="_blank" rel="noreferrer">VoidDocs</a> - Setup / troubleshoot almost anything.</li><li><a href="https://linux-kernel-labs.github.io/refs/heads/master/lectures/intro.html" target="_blank" rel="noreferrer">Linux Kernel Labs</a> - Guided way to learn about linux kernel with practical examples.</li><li><a href="https://elixir.bootlin.com" target="_blank" rel="noreferrer">Elixir Cross Referencer</a> - Extremely fast linux and other core utilities source code explorer with tag switching and other features.</li></ul><h3 id="rust" tabindex="-1">Rust <a class="header-anchor" href="#rust" aria-label="Permalink to &quot;Rust&quot;">​</a></h3><ul><li><a href="https://rust-lang-nursery.github.io/rust-cookbook/intro.html" target="_blank" rel="noreferrer">Rust Cookbook</a> - A collection of simple examples that demonstrate good practices to accomplish common programming tasks, using the crates of the Rust ecosystem.</li></ul><h3 id="ux" tabindex="-1">UX <a class="header-anchor" href="#ux" aria-label="Permalink to &quot;UX&quot;">​</a></h3><ul><li><a href="https://the-ux-mini-course.com" target="_blank" rel="noreferrer">The UX Mini Course</a> - From <a href="https://cubicle6.com" target="_blank" rel="noreferrer">https://cubicle6.com</a>, summarises his 20 years of UX experience.</li></ul><h3 id="system-design" tabindex="-1">System Design <a class="header-anchor" href="#system-design" aria-label="Permalink to &quot;System Design&quot;">​</a></h3><ul><li><a href="https://github.com/Jeevan-kumar-Raj/Grokking-System-Design" target="_blank" rel="noreferrer">Grokking System Design</a> - Short notes on system design concepts.</li></ul><h3 id="image-processing" tabindex="-1">Image Processing <a class="header-anchor" href="#image-processing" aria-label="Permalink to &quot;Image Processing&quot;">​</a></h3><ul><li><a href="https://youtu.be/BQvBq3K50u8" target="_blank" rel="noreferrer">How I animate stuffs on desmos graphing calculator</a>.</li><li><a href="https://www.youtube.com/watch?v=GKsCWivmlHg" target="_blank" rel="noreferrer">All types of fourier transform in Python</a>.</li></ul>',23),l=[i];function s(n,h,c,u,m,g){return r(),a("div",null,l)}const p=e(o,[["render",s]]);export{f as __pageData,p as default};
